import React, { useState, useEffect, useRef } from 'react';
import { Send, Search, Users, User, Edit2, Trash2, Reply, Check, CheckCheck, Circle, Menu, LogOut, UserPlus, X, Phone, Mail } from 'lucide-react';

function ChatApp() {
  // Auth State
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [isLogin, setIsLogin] = useState(true);
  
  // Form States
  const [identifier, setIdentifier] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [authError, setAuthError] = useState('');
  
  // Chat States
  const [contacts, setContacts] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [messages, setMessages] = useState({});
  const [inputMessage, setInputMessage] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [replyTo, setReplyTo] = useState(null);
  const [editingMessage, setEditingMessage] = useState(null);
  const [showAddContact, setShowAddContact] = useState(false);
  const [searchUserId, setSearchUserId] = useState('');
  const [onlineUsers, setOnlineUsers] = useState({});
  const [typingUsers, setTypingUsers] = useState({});
  const [showSidebar, setShowSidebar] = useState(true);
  
  const messagesEndRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  // Mock user database (in real app, this would be on backend)
  const userDatabase = useRef([
    { id: 'user1', name: 'Alice Johnson', email: 'alice@example.com', phone: '1234567890', password: 'password123', uniqueId: 'AJ-1234' },
    { id: 'user2', name: 'Bob Smith', email: 'bob@example.com', phone: '0987654321', password: 'password123', uniqueId: 'BS-5678' },
    { id: 'user3', name: 'Charlie Brown', email: 'charlie@example.com', phone: '5555555555', password: 'password123', uniqueId: 'CB-9012' },
    { id: 'user4', name: 'Diana Prince', email: 'diana@example.com', phone: '1111111111', password: 'password123', uniqueId: 'DP-3456' }
  ]).current;

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, selectedChat]);

  // Simulate online users
  useEffect(() => {
    if (isAuthenticated) {
      const interval = setInterval(() => {
        const online = {};
        userDatabase.forEach(user => {
          if (user.id !== currentUser?.id) {
            online[user.id] = Math.random() > 0.3;
          }
        });
        setOnlineUsers(online);
      }, 5000);
      return () => clearInterval(interval);
    }
  }, [isAuthenticated, currentUser, userDatabase]);

  // Load User's Chats
  const loadUserChats = (userId) => {
    const existingChats = [
      {
        id: 'user2',
        name: 'Bob Smith',
        uniqueId: 'BS-5678',
        lastMessage: 'Hey, how are you?',
        timestamp: new Date(Date.now() - 3600000),
        unread: 2
      }
    ];
    
    const chatsWithDetails = existingChats.map(chat => {
      const user = userDatabase.find(u => u.id === chat.id);
      return {
        ...chat,
        isOnline: Math.random() > 0.5
      };
    });
    
    setContacts(chatsWithDetails);
    
    const msgs = {
      'user2': [
        { 
          id: 'm1', 
          senderId: 'user2', 
          text: 'Hey, how are you?', 
          timestamp: new Date(Date.now() - 3600000), 
          status: 'delivered'
        },
        { 
          id: 'm2', 
          senderId: 'user2', 
          text: 'Are you free this weekend?', 
          timestamp: new Date(Date.now() - 3000000), 
          status: 'delivered'
        }
      ]
    };
    setMessages(msgs);
  };

  // Handle Authentication
  const handleAuth = (e) => {
    e.preventDefault();
    setAuthError('');

    if (isLogin) {
      const user = userDatabase.find(u => 
        (u.email === identifier || u.phone === identifier) && u.password === password
      );

      if (user) {
        const userSession = {
          id: user.id,
          name: user.name,
          email: user.email,
          phone: user.phone,
          uniqueId: user.uniqueId
        };
        setCurrentUser(userSession);
        setIsAuthenticated(true);
        loadUserChats(user.id);
      } else {
        setAuthError('Invalid credentials. Try: alice@example.com / password123');
      }
    } else {
      if (!name || !identifier || !password) {
        setAuthError('All fields are required');
        return;
      }

      const existingUser = userDatabase.find(u => u.email === identifier || u.phone === identifier);
      if (existingUser) {
        setAuthError('User already exists. Please login.');
        return;
      }

      const newUser = {
        id: 'user' + Date.now(),
        name,
        email: identifier.includes('@') ? identifier : '',
        phone: !identifier.includes('@') ? identifier : '',
        password,
        uniqueId: name.substring(0, 2).toUpperCase() + '-' + Math.floor(1000 + Math.random() * 9000)
      };
      
      userDatabase.push(newUser);
      
      const userSession = {
        id: newUser.id,
        name: newUser.name,
        email: newUser.email,
        phone: newUser.phone,
        uniqueId: newUser.uniqueId
      };
      setCurrentUser(userSession);
      setIsAuthenticated(true);
    }
  };

  // Handle Logout
  const handleLogout = () => {
    setIsAuthenticated(false);
    setCurrentUser(null);
    setContacts([]);
    setMessages({});
    setSelectedChat(null);
    setIdentifier('');
    setPassword('');
    setName('');
  };

  // Search and Add Contact
  const handleSearchUser = () => {
    if (!searchUserId.trim()) return;

    const user = userDatabase.find(u => 
      u.uniqueId === searchUserId || 
      u.email === searchUserId || 
      u.phone === searchUserId
    );

    if (!user) {
      alert('User not found');
      return;
    }

    if (user.id === currentUser.id) {
      alert('Cannot add yourself');
      return;
    }

    const exists = contacts.find(c => c.id === user.id);
    if (exists) {
      alert('Contact already exists');
      setShowAddContact(false);
      setSearchUserId('');
      return;
    }

    const newContact = {
      id: user.id,
      name: user.name,
      uniqueId: user.uniqueId,
      email: user.email,
      phone: user.phone,
      lastMessage: '',
      timestamp: new Date(),
      unread: 0,
      isOnline: Math.random() > 0.5
    };

    setContacts(prev => [...prev, newContact]);
    setMessages(prev => ({ ...prev, [user.id]: [] }));
    setShowAddContact(false);
    setSearchUserId('');
    alert('Contact added successfully!');
  };

  // Select Chat
  const handleSelectChat = (contact) => {
    setSelectedChat(contact);
    
    if (messages[contact.id]) {
      setMessages(prev => ({
        ...prev,
        [contact.id]: prev[contact.id].map(msg => ({
          ...msg,
          status: msg.senderId !== currentUser.id ? 'read' : msg.status
        }))
      }));
    }
    
    setContacts(prev => prev.map(c => 
      c.id === contact.id ? { ...c, unread: 0 } : c
    ));
  };

  // Send Message
  const handleSendMessage = () => {
    if (!inputMessage.trim() || !selectedChat) return;

    const newMessage = {
      id: 'msg_' + Date.now(),
      senderId: currentUser.id,
      text: inputMessage,
      timestamp: new Date(),
      status: 'sent',
      replyTo: replyTo?.id,
      edited: editingMessage ? true : false
    };

    if (editingMessage) {
      setMessages(prev => ({
        ...prev,
        [selectedChat.id]: prev[selectedChat.id].map(msg =>
          msg.id === editingMessage.id 
            ? { ...msg, text: inputMessage, edited: true } 
            : msg
        )
      }));
      setEditingMessage(null);
    } else {
      setMessages(prev => ({
        ...prev,
        [selectedChat.id]: [...(prev[selectedChat.id] || []), newMessage]
      }));
      
      setContacts(prev => prev.map(c =>
        c.id === selectedChat.id
          ? { ...c, lastMessage: inputMessage, timestamp: new Date() }
          : c
      ));

      setTimeout(() => {
        setMessages(prev => ({
          ...prev,
          [selectedChat.id]: prev[selectedChat.id].map(msg =>
            msg.id === newMessage.id ? { ...msg, status: 'delivered' } : msg
          )
        }));
      }, 1000);
    }

    setInputMessage('');
    setReplyTo(null);
  };

  // Handle Typing
  const handleTyping = (e) => {
    setInputMessage(e.target.value);
    
    if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);
    
    setTypingUsers(prev => ({ ...prev, [selectedChat.id]: true }));
    
    typingTimeoutRef.current = setTimeout(() => {
      setTypingUsers(prev => ({ ...prev, [selectedChat.id]: false }));
    }, 1000);
  };

  // Delete Message
  const handleDeleteMessage = (msgId, deleteForEveryone) => {
    setMessages(prev => ({
      ...prev,
      [selectedChat.id]: prev[selectedChat.id].map(msg =>
        msg.id === msgId
          ? { ...msg, deleted: true, deletedForEveryone }
          : msg
      )
    }));
  };

  const filteredContacts = contacts.filter(contact =>
    contact.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    contact.uniqueId.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const sortedContacts = [...filteredContacts].sort((a, b) => 
    new Date(b.timestamp) - new Date(a.timestamp)
  );

  const currentMessages = selectedChat ? (messages[selectedChat.id] || []) : [];

  if (!isAuthenticated) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="bg-white p-8 rounded-2xl shadow-2xl w-full max-w-md">
          <div className="text-center mb-8">
            <div className="bg-indigo-600 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
              <Users className="text-white" size={32} />
            </div>
            <h1 className="text-3xl font-bold text-gray-800">ChatConnect</h1>
            <p className="text-gray-600 mt-2">Connect instantly with anyone</p>
          </div>

          <form onSubmit={handleAuth}>
            {!isLogin && (
              <div className="mb-4">
                <label className="block text-gray-700 mb-2 font-medium">Full Name</label>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required={!isLogin}
                  placeholder="John Doe"
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                />
              </div>
            )}

            <div className="mb-4">
              <label className="block text-gray-700 mb-2 font-medium">
                Email or Phone Number
              </label>
              <div className="relative">
                <input
                  type="text"
                  value={identifier}
                  onChange={(e) => setIdentifier(e.target.value)}
                  required
                  placeholder="email@example.com or 1234567890"
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                />
                {identifier.includes('@') ? (
                  <Mail className="absolute right-3 top-3.5 text-gray-400" size={20} />
                ) : identifier.length > 0 ? (
                  <Phone className="absolute right-3 top-3.5 text-gray-400" size={20} />
                ) : null}
              </div>
            </div>

            <div className="mb-6">
              <label className="block text-gray-700 mb-2 font-medium">Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                placeholder="••••••••"
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
              />
            </div>

            {authError && (
              <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-lg text-sm">
                {authError}
              </div>
            )}

            <button
              type="submit"
              className="w-full bg-indigo-600 text-white py-3 rounded-lg font-semibold hover:bg-indigo-700 transition-colors"
            >
              {isLogin ? 'Login' : 'Register'}
            </button>
          </form>

          <p className="text-center mt-6 text-gray-600">
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button
              onClick={() => {
                setIsLogin(!isLogin);
                setAuthError('');
              }}
              className="text-indigo-600 font-semibold hover:underline"
            >
              {isLogin ? 'Register' : 'Login'}
            </button>
          </p>

          {isLogin && (
            <div className="mt-4 p-3 bg-blue-50 rounded-lg text-sm text-gray-600">
              <p className="font-semibold mb-1">Demo Accounts:</p>
              <p>Email: alice@example.com</p>
              <p>Password: password123</p>
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-100">
      <div className={`${showSidebar ? 'w-80' : 'w-0'} bg-white border-r border-gray-200 flex flex-col transition-all duration-300 overflow-hidden`}>
        <div className="bg-indigo-600 text-white p-4">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-white rounded-full flex items-center justify-center">
                <User className="text-indigo-600" size={24} />
              </div>
              <div>
                <h2 className="font-semibold">{currentUser.name}</h2>
                <p className="text-xs opacity-90">ID: {currentUser.uniqueId}</p>
              </div>
            </div>
            <button 
              onClick={handleLogout}
              className="hover:bg-indigo-700 p-2 rounded-lg transition-colors"
              title="Logout"
            >
              <LogOut size={20} />
            </button>
          </div>

          <div className="relative">
            <Search className="absolute left-3 top-3 text-indigo-300" size={18} />
            <input
              type="text"
              placeholder="Search chats..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-2 rounded-lg bg-indigo-700 text-white placeholder-indigo-300 focus:outline-none focus:ring-2 focus:ring-white"
            />
          </div>
        </div>

        <div className="p-3 border-b border-gray-200">
          <button
            onClick={() => setShowAddContact(true)}
            className="w-full flex items-center justify-center gap-2 bg-indigo-50 text-indigo-600 py-2 rounded-lg hover:bg-indigo-100 transition-colors"
          >
            <UserPlus size={18} />
            <span className="text-sm font-medium">Add New Contact</span>
          </button>
        </div>

        <div className="flex-1 overflow-y-auto">
          {sortedContacts.length === 0 ? (
            <div className="p-8 text-center text-gray-500">
              <Users size={48} className="mx-auto mb-3 opacity-50" />
              <p className="font-medium">No chats yet</p>
              <p className="text-sm mt-1">Add a contact to start chatting</p>
            </div>
          ) : (
            sortedContacts.map(contact => (
              <div
                key={contact.id}
                onClick={() => handleSelectChat(contact)}
                className={`p-4 border-b border-gray-100 cursor-pointer hover:bg-gray-50 transition-colors ${
                  selectedChat?.id === contact.id ? 'bg-indigo-50' : ''
                }`}
              >
                <div className="flex items-center gap-3">
                  <div className="relative">
                    <div className="w-12 h-12 bg-indigo-100 rounded-full flex items-center justify-center">
                      <User className="text-indigo-600" size={24} />
                    </div>
                    {onlineUsers[contact.id] && (
                      <Circle className="absolute bottom-0 right-0 text-green-500 fill-green-500" size={12} />
                    )}
                  </div>
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between">
                      <h3 className="font-semibold text-gray-800 truncate">{contact.name}</h3>
                      {contact.timestamp && (
                        <span className="text-xs text-gray-500">
                          {new Date(contact.timestamp).toLocaleTimeString([], { 
                            hour: '2-digit', 
                            minute: '2-digit' 
                          })}
                        </span>
                      )}
                    </div>
                    <div className="flex items-center justify-between">
                      <p className="text-sm text-gray-500 truncate">
                        {contact.lastMessage || contact.uniqueId}
                      </p>
                      {contact.unread > 0 && (
                        <span className="bg-indigo-600 text-white text-xs rounded-full px-2 py-0.5 ml-2">
                          {contact.unread}
                        </span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="flex-1 flex flex-col">
        {selectedChat ? (
          <>
            <div className="bg-white border-b border-gray-200 p-4 flex items-center justify-between">
              <div className="flex items-center gap-3">
                <button
                  onClick={() => setShowSidebar(!showSidebar)}
                  className="lg:hidden hover:bg-gray-100 p-2 rounded-lg"
                >
                  <Menu size={20} />
                </button>
                <div className="w-10 h-10 bg-indigo-100 rounded-full flex items-center justify-center">
                  <User className="text-indigo-600" size={20} />
                </div>
                <div>
                  <h2 className="font-semibold text-gray-800">{selectedChat.name}</h2>
                  <p className="text-sm text-gray-500">
                    {onlineUsers[selectedChat.id] ? 'Online' : 'Offline'}
                  </p>
                  {typingUsers[selectedChat.id] && (
                    <p className="text-xs text-indigo-600">typing...</p>
                  )}
                </div>
              </div>
            </div>

            <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-50">
              {currentMessages.length === 0 ? (
                <div className="flex items-center justify-center h-full">
                  <div className="text-center text-gray-500">
                    <p className="font-medium">No messages yet</p>
                    <p className="text-sm mt-1">Send a message to start the conversation</p>
                  </div>
                </div>
              ) : (
                currentMessages.map(msg => {
                  const isOwn = msg.senderId === currentUser.id;
                  const replyToMsg = msg.replyTo 
                    ? currentMessages.find(m => m.id === msg.replyTo)
                    : null;

                  return (
                    <div key={msg.id} className={`flex ${isOwn ? 'justify-end' : 'justify-start'}`}>
                      <div className={`max-w-md ${isOwn ? 'bg-indigo-600 text-white' : 'bg-white text-gray-800'} rounded-lg p-3 shadow-sm`}>
                        {replyToMsg && (
                          <div className={`text-xs mb-2 p-2 rounded ${isOwn ? 'bg-indigo-700' : 'bg-gray-100'} border-l-2 ${isOwn ? 'border-white' : 'border-indigo-600'}`}>
                            <Reply size={12} className="inline mr-1" />
                            <span className="opacity-80">{replyToMsg.text}</span>
                          </div>
                        )}

                        {msg.deleted ? (
                          <p className="italic opacity-70">
                            {msg.deletedForEveryone ? 'This message was deleted' : 'You deleted this message'}
                          </p>
                        ) : (
                          <>
                            <p className="break-words">{msg.text}</p>
                            {msg.edited && <span className="text-xs opacity-70 ml-2">(edited)</span>}
                          </>
                        )}

                        <div className="flex items-center justify-between mt-2 gap-3">
                          <span className="text-xs opacity-70">
                            {new Date(msg.timestamp).toLocaleTimeString([], { 
                              hour: '2-digit', 
                              minute: '2-digit' 
                            })}
                          </span>

                          {isOwn && !msg.deleted && (
                            <div className="flex items-center gap-2">
                              {msg.status === 'sent' && <Check size={14} />}
                              {msg.status === 'delivered' && <CheckCheck size={14} />}
                              {msg.status === 'read' && <CheckCheck size={14} className="text-blue-300" />}

                              <button
                                onClick={() => setReplyTo(msg)}
                                className="hover:opacity-70"
                                title="Reply"
                              >
                                <Reply size={14} />
                              </button>

                              <button
                                onClick={() => {
                                  setEditingMessage(msg);
                                  setInputMessage(msg.text);
                                }}
                                className="hover:opacity-70"
                                title="Edit"
                              >
                                <Edit2 size={14} />
                              </button>

                              <button
                                onClick={() => {
                                  const deleteForEveryone = window.confirm(
                                    'Delete for everyone? Click Cancel to delete for you only.'
                                  );
                                  handleDeleteMessage(msg.id, deleteForEveryone);
                                }}
                                className="hover:opacity-70"
                                title="Delete"
                              >
                                <Trash2 size={14} />
                              </button>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  );
                })
              )}
              <div ref={messagesEndRef} />
            </div>

            <div className="bg-white border-t border-gray-200 p-4">
              {replyTo && (
                <div className="mb-2 p-2 bg-gray-100 rounded-lg flex items-center justify-between">
                  <div className="flex items-center gap-2 text-sm text-gray-600">
                    <Reply size={16} />
                    <span className="truncate">Replying to: {replyTo.text}</span>
                  </div>
                  <button 
                    onClick={() => setReplyTo(null)}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    <X size={16} />
                  </button>
                </div>
              )}

              {editingMessage && (
                <div className="mb-2 p-2 bg-blue-100 rounded-lg flex items-center justify-between">
                  <div className="flex items-center gap-2 text-sm text-blue-600">
                    <Edit2 size={16} />
                    <span>Editing message</span>
                  </div>
                  <button 
                    onClick={() => {
                      setEditingMessage(null);
                      setInputMessage('');
                    }}
                    className="text-blue-600 hover:text-blue-800"
                  >
                    <X size={16} />
                  </button>
                </div>
              )}

              <div className="flex items-center gap-2">
                <input
                  type="text"
                  value={inputMessage}
                  onChange={handleTyping}
                  onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && handleSendMessage()}
                  placeholder="Type a message..."
                  className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                />
                <button
                  onClick={handleSendMessage}
                  disabled={!inputMessage.trim()}
                  className="bg-indigo-600 text-white p-3 rounded-lg hover:bg-indigo-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  <Send size={20} />
                </button>
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center bg-gray-50">
            <div className="text-center">
              <Users className="mx-auto text-gray-400 mb-4" size={64} />
              <h2 className="text-2xl font-semibold text-gray-600">
                Select a chat to start messaging
              </h2>
              <p className="text-gray-500 mt-2">
                Choose a conversation from the sidebar
              </p>
            </div>
          </div>
        )}
      </div>

      {showAddContact && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-bold flex items-center gap-2">
                <UserPlus size={24} />
                Add New Contact
              </h2>
              <button 
                onClick={() => {
                  setShowAddContact(false);
                  setSearchUserId('');
                }}
                className="text-gray-500 hover:text-gray-700"
              >
                <X size={24} />
              </button>
            </div>

            <input
              type="text"
              placeholder="Enter ID, email, or phone number"
              value={searchUserId}
              onChange={(e) => setSearchUserId(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearchUser()}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />

            <div className="mb-4 p-3 bg-gray-50 rounded-lg text-sm text-gray-600">
              <p className="font-semibold mb-1">Try adding:</p>
              <p>ID: BS-5678 (Bob Smith)</p>
              <p>ID: CB-9012 (Charlie Brown)</p>
              <p>ID: DP-3456 (Diana Prince)</p>
            </div>

            <div className="flex gap-2">
              <button
                onClick={handleSearchUser}
                className="flex-1 bg-indigo-600 text-white py-2 rounded-lg hover:bg-indigo-700"
              >
                Add Contact
              </button>
              <button
                onClick={() => {
                  setShowAddContact(false);
                  setSearchUserId('');
                }}
                className="flex-1 bg-gray-300 text-gray-700 py-2 rounded-lg hover:bg-gray-400"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ChatApp;
